package com.kitchen.arrays;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * LC Link : https://leetcode.com/problems/substring-with-concatenation-of-all-words/description/
 */

public class SubstringWithConcatenationOfAllWords {
    public static void main(String[] args) {
        String s = "";
        String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};

        List<Integer> result = findSubstring(s, words);
    }

    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> res = new ArrayList<Integer>();
        if (words == null || words.length == 0 || "".equals(s)){
            return res;
        }
        Integer total = words[0].length() * words.length;
        if (s.length() < total){
            return res;
        }
        int increment = words[0].length();


        for (int start=0; start < s.length(); start++){
            int begin = start;
            int end = start;
            Map<String,Integer> freq = new HashMap<>();
            for(int i=0; i < words.length; i++){
                Integer count = freq.get(words[i]);
                freq.put(words[i], count != null ? ++count : 1);
            }
            int counter = freq.size();
            while(end + increment - 1 < s.length()){
                String endStr = s.substring(end, end + increment);
                if (freq.containsKey(endStr)){
                    int count = freq.get(endStr);
                    freq.put(endStr, --count);
                    if (count == 0){
                        counter--;
                    }
                }
                end += increment;

                while (counter == 0){
                    if (end - begin == total && !res.contains(begin)){
                        res.add(begin);
                    }
                    String beginStr = s.substring(begin, begin+increment);
                    if (freq.containsKey(beginStr)){
                        int count = freq.get(beginStr);
                        freq.put(beginStr, ++count);
                        if (count > 0){
                            counter++;
                        }
                    }
                    begin += increment;
                }
            }
        }


        return res;
    }
}
